import { Component } from "react";
import axios from "axios";
import Spinner from "../spinner/spinner";
import noPoster from '../../images/noPoster.png'
import clock from '../../images/clock.png'
import star from '../../images/star.png'
import FavButton from "../favButton/favButton";
import { Row, Col, Image, Container } from 'react-bootstrap';
import './moviePage.css'

const apikey = "37e7a5cb2b4f66257a0b6632a59060a5";

const POSTER_SIZE = 'w500/'

class MoviePage extends Component{
    constructor(props) {
        super(props)
        this.state = {
            ID: -1,
            data: null,
            isFav: false,
            imgLoaded: false,
            server: process.env.REACT_APP_SERVER_URL || ''
        }
    }

    componentDidMount() {
        axios.get('http://localhost:4000/movies/')
            .then(response => {
                this.setState({ favMovies: response.data });
            })
            .catch(function (error){
                console.log(error);
            })
        }

        getFavMovies(){
            if (this.state.favMovies.length === 0)
            {
                return <div className="noResult"><br/><p>No Movies Favorited Yet!</p></div>;
            }
            else
            {
                return this.state.favMovies.map(currentMovie => (
                    <div className="movieContent mx-5" key={currentMovie._id}>
                        {(currentMovie.poster_path!=null)?
                            (<div className="poster">
                                <img src={"https://image.tmdb.org/t/p/w185"+currentMovie.poster_path+""} alt={currentMovie.title}/>
                            </div>):
                            (<div className="poster"/>)
                        }
                        <div className="movieData">
                            <span>
                                <span style={{fontSize:'2em'}}>{currentMovie.title}  </span>
                                <br/>
                                <span style={{fontStyle: "italic", color: "grey", fontSize:"1.25em"}}>{currentMovie.genres.map(gObj =>{return gObj.name + " "})}</span>
                                </span><br/>
                                <p>{currentMovie.overview}</p>
                                <br/>
                                <button onClick={this.removeMovie} id={currentMovie._id} type="button" className="btn, btn-danger">Remove From Favorites</button>
                        </div>
                    </div>
                ));
            }
        }

        async removeMovie(e) {
            e.preventDefault();
            await axios.delete('http://localhost:4000/movies/remove/'+e.target.id).catch(function(error) {
                console.log("DELETE error: "+error);
            });
            await axios.get('http://localhost:4000/movies/')
                .then(response => {
                    this.setState({ favMovies: response.data });
                })
                .catch(function (error){
                console.log("GET error: "+error);
            });
        }
        addToFav(e) {
            alert(e.target.id)
        }


    getData = () =>{
        //const URL = this.state.server+`/api/movies/getmovie/${this.state.ID}`
        const URL = "https://api.themoviedb.org/3/genre/movie/list?api_key="+ apikey +"&language=en-US"

        axios.get(URL)
        .then((response) => {
            let myGenres = ''
            for (let i = 0; i < response.data.genres.length-1; i++) {
                myGenres += response.data.genres[i].name + ', '
            }
            myGenres += response.data.genres[response.data.genres.length-1].name

            this.setState({
                data: response.data,
                genres: myGenres
            })
        })
        .catch(err =>{
            console.log(err)
        });
    }

    componentDidMount = () =>{
        this.setState({
            ID: this.props.match.params.id,
            isFav: this.props.location.state.isFav
        }, () => {
            this.getData()
        })
    }

    render(){
        if (this.state.data){

            let poster_url
            (this.state.data.poster_path == null) ? poster_url= noPoster : poster_url = 'https://image.tmdb.org/t/p/'+POSTER_SIZE + this.state.data.poster_path

            return(
                <Container fluid='lg' className='text-center movie'>
                    <Row xl={2} lg={1} md={1} sm={1} xs={1}>
                            <Col id='img'>
                                <Image 
                                    thumbnail
                                    src={poster_url} 
                                    fluid className='tile-img' 
                                    onLoad={() => this.setState({imgLoaded: true})}
                                    style={this.state.imgLoaded ? {} : {display: 'none'}}
                                />
                            </Col>
                            <Col id='info'>
                                <h1 className="title">{this.state.data.title}</h1>
                                <br/>
                                <h4 className="title">{this.state.data.overview}</h4>
                                <br/>
                                <h4 className="title">{this.state.genres}</h4>
                                <br/>
                                <Row>
                                    <Col sm={3} className='inline-pic'>
                                        <h5> <img src={clock} alt='clock' style={{height: '2.0em'}}/> {parseInt(this.state.data.runtime/60)}:{this.state.data.runtime%60}</h5>
                                    </Col>
                                    <Col sm={3} className='inline-pic'>
                                        <h5> <img src={star} alt='star' style={{height: '2.0em'}}/> {this.state.data.vote_average.toFixed(1)}/10</h5>
                                    </Col>
                                    <Col sm={6}>
                                        <div className="d-grid gap-2">
                                            {<FavButton isFav={this.state.isFav} id={this.state.ID}/>}
                                        </div>
                                    </Col>
                                </Row>
                            </Col>
                     </Row>
                </Container>
            )
        }
        else{
            return(<Spinner/>)
        }

    }
}

export default MoviePage
