import React, { useState } from "react";
import { useNavigate } from "react-router";

// import DatePicker from "react-datepicker";

// import "react-datepicker/dist/react-datepicker.css";

// import TextField from '@mui/material/TextField';
// import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
// import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
// import { DatePicker } from '@mui/x-date-pickers/DatePicker';

// import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Rating from '@mui/material/Rating';

import TextField from '@mui/material/TextField';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { format } from 'date-fns';

export default function Create() {
  const [rating, setRating] = React.useState(2);
  const [value1, setValue1] = React.useState(null);

  const updateRating = (newRating) => {
    setRating(newRating);
    console.log(rating);
  }
  const [inputs, setInputs] = React.useState({});

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const r = ratingRef.current.value;
    const d = dateRef.current.value;
    var dt = new Date(+d);
    alert(r + ' ' + format(dt, 'MM/dd/yyyy'));
  }

  const dateRef = React.useRef();
  const ratingRef = React.useRef();

  const [form, setForm] = useState({
    name: "",
    position: "",
    date: "",
  });
  const navigate = useNavigate();

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();
    const d = dateRef.current.value;
    var dt = new Date(+d);

    // alert(format(dt, 'MM/dd/yyyy'));

    // setForm({date: format(dt, 'MM/dd/yyyy')});

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newPerson = { ...form };
    newPerson.date = format(dt, 'MM/dd/yyyy');

    await fetch("http://localhost:4000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPerson),
    })
    .catch(error => {
      window.alert(error);
      return;
    });

    setForm({ name: "", position: "", date: "", level: "" });
    navigate("/");
  }

  // This following section will display the form that takes the input from the user.
  return (
    <div style={{marginTop: 50, marginLeft: 300, marginRight: 300}}>
      <h3>NEW REVIEW</h3>
      {/* <DatePicker selected={startDate} onChange={(date) => setStartDate(date)} /> */}
      {/* <LocalizationProvider dateAdapter={AdapterDayjs}>
      <DatePicker
        label="Basic example"
        value={value}
        onChange={(newValue) => {
          setValue(newValue);
        }}
        renderInput={(params) => <TextField {...params} />}
      />
    </LocalizationProvider> */}
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="name">I WATCHED...</label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={form.name}
            onChange={(e) => updateForm({ name: e.target.value })}
          />
        </div>

        <div className="form-group row">
          <label htmlFor="date" className="col-form-label" style={{marginLeft: 15}} >ON:</label>
          {/* <div className="col-sm-1"> */}
            {/* <input
              type="text"
              className="form-control"
              placeholder="dd/mm/yyyy"
              id="date"
              value={form.date}
              onChange={(e) => updateForm({ date: e.target.value })}
            /> */}
          {/* </div> */}

          <input
            name="entry"
            type="text"
            value={value1}
            ref={dateRef}
            hidden
            readOnly
          />

          <LocalizationProvider dateAdapter={AdapterDayjs}>
          <DatePicker
            label="date"
            mask="__/__/____"
            value={value1}
            onChange={(newValue) => {
              setValue1(newValue);
            }}
            renderInput={(params) => <TextField {...params} />}
          />
          </LocalizationProvider>

        </div>

        <div className="form-group">
          <textarea
            type="text"
            className="form-control"
            rows="5"
            placeholder="ADD A REVIEW..."
            id="position"
            value={form.position}
            onChange={(e) => updateForm({ position: e.target.value })}
          />
        </div>

        <div className="form-group text-right">
          <a href="/" class="btn btn-secondary mr-3" role="button">CANCEL</a>
          <input
            type="submit"
            value="LOG"
            className="btn btn-primary"
          />
        </div>
      </form>

      <div>
        <form onSubmit={handleSubmit}>
        <label>Enter your name:
        <input
          type="text"
          name="username"
          value={inputs.username || ""}
          onChange={handleChange}
        />
        </label>

        <label>Enter your age:
          <input
            type="number"
            name="age"
            value={inputs.age || ""}
            onChange={handleChange}
          />
          </label>

          <input
            name="rating"
            type="number"
            value={rating}
            ref={ratingRef}
            hidden
            readOnly
          />

          <input
            name="entry"
            type="text"
            value={value1}
            ref={dateRef}
            hidden
            readOnly
          />

          <Rating
            name="simple-controlled"
            value={rating}
            onChange={(event, newValue) => updateRating(newValue)
            }
          />

        {/* <LocalizationProvider dateAdapter={AdapterDayjs}>
        <DatePicker
          label="Basic example"
          mask="__/__/____"
          value={value1}
          onChange={(newValue) => {
            setValue1(newValue);
          }}
          renderInput={(params) => <TextField {...params} />}
        />
        </LocalizationProvider> */}

      <input type="submit" />
      </form>
    </div>
    </div>
  );
}
