import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./index.css";
import { Button, Modal } from 'react-bootstrap';

// We use Route in order to define the different routes of our application
import { Route, Routes, BrowserRouter, Switch} from "react-router-dom";

// We import all the components we need in our app
import Navbar from "./components/navbar";

import Diary from "./components/diary";
import Edit from "./components/edit";
import Create from "./components/create";
import Review from "./components/review";
import Search from "./components/search/search";

import Lists from "./components/lists";
import EditList from "./components/editList";
import CreateList from "./components/createList";
import ViewList from "./components/viewList";

import Login from "./components/login";
import Register from "./components/register";
import Reset from "./components/reset";
import Dashboard from "./components/dashboard";

export const ProtectedRoute = ({ component, path, exact }) => {
  return (
    <FirebaseAuthConsumer>
      {({ isSignedIn }) => {
        if (isSignedIn) {
          return <Route component={component} path={path} exact={exact} />;
        } else {
          return <Redirect to="/" />;
        }
      }}
    </FirebaseAuthConsumer>
  );
};

const App = () => {

  return (
    <div>
      <Navbar />
      <div style={{ margin: 20 }}>
      <Routes>
        <Route exact path="/" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/reset" element={<Reset />} />
        <Route path="/dashboard" element={<Dashboard />} />

        <Route path="/diary" element={<Diary />} />
        <ProtectedRoute path="/edit/:id" element={<Edit />} />
        <ProtectedRoute path="/create" element={<Create />} />
        <ProtectedRoute path="/review/:id" element={<Review />} />
        <ProtectedRoute path="/search" element={<Search />} />

        <ProtectedRoute path="/lists" element={<Lists />} />
        <ProtectedRoute path="/editlist/:id" element={<EditList />} />
        <ProtectedRoute path="/createlist" element={<CreateList />} />
        <ProtectedRoute path="/list/:id" element={<ViewList />} />


      </Routes>
      </div>
    </div>

  );
};

export default App;
