import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Grid } from "@material-ui/core";

const apikey = "37e7a5cb2b4f66257a0b6632a59060a5";

export default class MoviePage extends Component {

	constructor(props) {
		super(props);

		this.onChangeSearchValue = this.onChangeSearchValue.bind(this);
		this.onSubmit = this.onSubmit.bind(this);
		this.getMovieSearch = this.getMovieSearch.bind(this);
		this.addToFav = this.addToFav.bind(this);
		this.getGenres();

		this.state = {
			search_value: '',
			search_results: [],
            movies: [],
            showCrawl: {},
		}
	}

    componentDidMount() {
        this.handleMovies();
    }

    handleMovies = () =>
        fetch("https://api.themoviedb.org/3/search/movie?api_key=" + apikey + "&language=en-US&query=")
          .then(results => results.json())
          .then(data => this.setState({ movies: data.results }));

    handleCrawl = e => {
        const { id } = e.target;
        this.setState(current => ({
          showCrawl: { ...current.showCrawl, [id]: !current.showCrawl[id] }
        }));
    };


    render() {
      return (
        <div>
          <h1>Episodes</h1>
          <div>
            {this.state.movies.map(movie => (
              <div
                key={movie.episode_id}
                id={movie.episode_id}
                onClick={this.handleCrawl}
              >
                {movie.title}
                {this.state.showCrawl[movie.episode_id] && (
                  <div style={{ border: "1px black solid" }}>
                    {movie.opening_crawl}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      );
    }
  }
