import React, { Component, useState, useRef } from 'react';
import axios from 'axios';

import Movies from './movies';
import { search } from './utils';
import TextField from '@mui/material/TextField';

function withMyHook(Component) {
  return function WrappedComponent(props) {
    const myHookValue = React.useRef();
    return <Component {...props} myHookValue={myHookValue} />;
  }
}


class Search extends Component {
  state = {
    movies: null,
    loading: false,
    value: ''
  };

  search = async val => {
    this.setState({ loading: true });
    const res = await axios(
      `https://api.themoviedb.org/3/search/movie?query=${val}&api_key=dbc0a6d62448554c27b6167ef7dabb1b`
    );
    const movies = await res.data.results;
    // document.getElementById('searchresults').style.visibility = "visible";

    this.setState({ movies, loading: false });
  };

  onChangeHandler = async e => {
    this.search(e.target.value);
    this.setState({ value: e.target.value });
    console.log(e.target.value);
    // document.getElementById('searchresults').style.visibility = "visible";
  };

  onClickHandler = async e => {
    // document.getElementById('searchresults').style.display = "block";
    this.setState({ value: e.target.value });
    // console.log(this.state.value);
  };

  get renderMovies() {
    if (this.state.movies) {
      return <Movies list={this.state.movies} />;
    }
  }

  render() {
    const myHookValue = this.props.myHookValue;
    return (
      <div style={{margin: 0}}>
      	{/* <div style={{marginTop: 100, marginLeft: 300, marginRight: 300}}> */}
        {/* <input
          value={this.state.value}
          onChange={e => this.onChangeHandler(e)}
          placeholder="Search Here"
        /> */}
        <TextField
          fullWidth
          id="searchbar"
          variant="outlined" 
          value={this.state.value}
          ref={myHookValue}
          onChange={e => this.onChangeHandler(e)}
          onClick={() => {
            this.state.value = myHookValue;
            console.log(myHookValue)
          }}
          placeholder="Search for Films..."
          required
          size="small"
        />
        <div>{this.renderMovies}</div>
      </div>
	  // </div>
    );
  }
}

export default withMyHook(Search);
