import React, { useState, useEffect } from "react";
import { useParams} from "react-router"; //useNavigate } from "react-router";

import { useHistory } from 'react-router-dom';


const history = useHistory();

const user = firebase.auth().currentUser;

useEffect(() => {
  if (!user) {
    history('/clients');
  }
}, [user]);

export default function Edit() {
  const [form, setForm] = useState({
    name: "",
    position: "",
    date: "",
    // level: "",
    records: [],
  });
  const params = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const id = params.id.toString();
      const response = await fetch(`http://localhost:4000/record/${params.id.toString()}`);

      if (!response.ok) {
        const message = `An error has occured: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const record = await response.json();
      if (!record) {
        window.alert(`Record with id ${id} not found`);
        navigate("/");
        return;
      }

      setForm(record);
    }

    fetchData();

    return;
  }, [params.id, navigate]);

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  async function onSubmit(e) {
    e.preventDefault();
    const editedPerson = {
      name: form.name,
      position: form.position,
      date: form.date,
      level: form.level,
    };

    // This will send a post request to update the data in the database.
    await fetch(`http://localhost:4000/update/${params.id}`, {
      method: "POST",
      body: JSON.stringify(editedPerson),
      headers: {
        'Content-Type': 'application/json'
      },
    });

    navigate("/");
  }



  // This following section will display the form that takes input from the user to update the data.
  return (
    <div style={{marginTop: 50, marginLeft: 300, marginRight: 300}}>
      <h3>Edit Review</h3>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="name">I WATCHED... </label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={form.name}
            onChange={(e) => updateForm({ name: e.target.value })}
          />
        </div>
        <div className="form-group row">
          <label htmlFor="date" className="col-form-label" style={{marginLeft: 15}} >ON:</label>
          <div className="col-sm-3">
            <input
              type="text"
              className="form-control"
              // placeholder="dd/mm/yyyy"
              id="date"
              value={form.date}
              onChange={(e) => updateForm({ date: e.target.value })}
            />
          </div>
        </div>
        <div className="form-group">
          {/* <label htmlFor="position">Position: </label> */}
          <textarea
            type="text"
            className="form-control"
            id="position"
            rows="5"
            value={form.position}
            onChange={(e) => updateForm({ position: e.target.value })}
          />
        </div>
        {/* <div className="form-group">
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="positionOptions"
              id="positionIntern"
              value="Intern"
              checked={form.level === "Intern"}
              onChange={(e) => updateForm({ level: e.target.value })}
            />
            <label htmlFor="positionIntern" className="form-check-label">Intern</label>
          </div>
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="positionOptions"
              id="positionJunior"
              value="Junior"
              checked={form.level === "Junior"}
              onChange={(e) => updateForm({ level: e.target.value })}
            />
            <label htmlFor="positionJunior" className="form-check-label">Junior</label>
          </div>
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="positionOptions"
              id="positionSenior"
              value="Senior"
              checked={form.level === "Senior"}
              onChange={(e) => updateForm({ level: e.target.value })}
            />
            <label htmlFor="positionSenior" className="form-check-label">Senior</label>
        </div>
        </div>
        <br /> */}

        <div className="form-group text-right">
          {/* <input
            type="submit"
            value="Delete"
            className="btn btn-secondary mr-3"
          /> */}

          {/* <button className="btn btn-secondary mr-3"
            onClick={() => {
              // props.deleteRecord(props.record._id);
            }}
          >Delete</button> */}

          <input
            type="submit"
            value="Save"
            className="btn btn-primary"
          />
        </div>
      </form>
    </div>
  );
}
