import React, { useState } from "react";
import { useNavigate } from "react-router";

export default function Create() {
  const [form, setForm] = useState({
    name: "",
    position: "",
    date: "",
    // level: "",
  });
  const navigate = useNavigate();

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newPerson = { ...form };

    await fetch("http://localhost:4000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPerson),
    })
    .catch(error => {
      window.alert(error);
      return;
    });

    setForm({ name: "", position: "", date: "", level: "" });
    navigate("/");
  }

  // This following section will display the form that takes the input from the user.
  return (
    <div style={{marginTop: 50, marginLeft: 300, marginRight: 300}}>
      <h3>NEW REVIEW</h3>
      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="name">I WATCHED...</label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={form.name}
            onChange={(e) => updateForm({ name: e.target.value })}
          />
        </div>
        <div className="form-group row">
          <label htmlFor="date" className="col-form-label" style={{marginLeft: 15}} >ON:</label>
          <div className="col-sm-3">
            <input
              type="text"
              className="form-control"
              placeholder="dd/mm/yyyy"
              id="date"
              value={form.date}
              onChange={(e) => updateForm({ date: e.target.value })}
            />
          </div>
        </div>
        <div className="form-group">
          {/* <label htmlFor="position"></label> */}
          <textarea
            type="text"
            className="form-control"
            rows="5"
            placeholder="ADD A REVIEW..."
            id="position"
            value={form.position}
            onChange={(e) => updateForm({ position: e.target.value })}
          />
        </div>

