import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import { Link } from "react-router-dom";
import poster from './poster.jpg';

import Rating from '@mui/material/Rating';

import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';

import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

import { createTheme, ThemeProvider } from '@mui/material/styles';

const theme = createTheme({
  status: {
    danger: '#e53e3e',
  },
  palette: {
    primary: {
      main: '#0971f1',
      darker: '#053e85',
    },
    neutral: {
      main: '#64748B',
      contrastText: '#fff',
    },
  },
});

export default function View() {
  // dropdown menu
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
      setAnchorEl(null);
  };

  // confirm delete alert
  const [openAlert, setOpenAlert] = React.useState(false);

  const handleClickOpenAlert = () => {
    setAnchorEl(null);
    setOpenAlert(true);
  };

  const handleCloseAlert = () => {
    setOpenAlert(false);
  };

  // rating and date
  const [rating, setRating] = React.useState(null);
  const [value1, setValue1] = React.useState(null);

  // review info
  const [form, setForm] = useState({
    name: "",
    position: "",
    date: "",
    rating: "",
    records: [],
  });
  const params = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const id = params.id.toString();
      const response = await fetch(`http://localhost:4000/record/${params.id.toString()}`);

      if (!response.ok) {
        const message = `An error has occured: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const record = await response.json();
      if (!record) {
        window.alert(`Record with id ${id} not found`);
        navigate("/");
        return;
      }

      setForm(record);
      setValue1(record.date);
      setRating(record.rating);
    }

    fetchData();

    return;
  }, [params.id, navigate]);

  // This method will delete a record
  async function deleteRecord(deleteId) {
      await fetch(`http://localhost:4000/${deleteId}`, {
          method: "DELETE"
      });
      navigate("/")
  }

  return (
    <div style={{marginTop: 50, marginLeft: 300, marginRight: 300}}>
    <div class="container">
    <h3>{form.name}
    <div class="float-right">
    <ThemeProvider theme={theme}>
      <Button color="neutral"
        id="basic-button"
        aria-controls={open ? 'basic-menu' : undefined}
        aria-haspopup="true"
        aria-expanded={open ? 'true' : undefined}
        onClick={handleClick}
        sx={{
          margin:0,
        }}
      >
        •••
      </Button>
      </ThemeProvider>
      <Menu
        id="basic-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          'aria-labelledby': 'basic-button',
        }}
      >

        <Link to={`/edit/${params.id}`} style={{ textDecoration: 'none', color: 'black'}}>
        <MenuItem>Edit</MenuItem></Link>

        <MenuItem onClick={handleClickOpenAlert}>Delete</MenuItem>
      </Menu> 
    </div>
    </h3>
    </div>

    <br></br>

    {/* poster */}
    <div class="container">
        <div class="row">
            <div class="col-4 nopadding">
                {/* placeholder poster for now!! */}
                <img src={poster} class="img-fluid"/>
            </div>
            <div class="col">
            <p> watched on {value1}
            <div class="float-right"><Rating
                    name="simple-controlled"
                    defaultValue={rating}
                    value={rating}
                    size="medium"
                    readOnly
                /></div>
            </p>
            <p>{form.position}</p> 
            </div>
        </div>
    </div> 

    {/* delete confirmation alert */}
    <Dialog
      open={openAlert}
      onClose={handleCloseAlert}
      aria-labelledby="alert-dialog-title"
      aria-describedby="alert-dialog-description"
    >
      <DialogTitle id="alert-dialog-title">
        {"Delete Confirmation"}
      </DialogTitle>
      <DialogContent>
        <DialogContentText id="alert-dialog-description">
          Are you sure you want to delete your review?
        </DialogContentText>
      </DialogContent>
      <DialogActions>
      <ThemeProvider theme={theme}>
        <Button color="neutral" onClick={handleCloseAlert}>Cancel</Button>
        <Button color="error" onClick={() => {
              deleteRecord(params.id);
          }} autoFocus>
          Delete
        </Button>
        </ThemeProvider>
      </DialogActions>
    </Dialog>    
    </div>
  );
}
