import React, { useState } from "react";
import defaultposter from './defaultposter.jpeg';

import classes from "./movie.module.css";
import { truncStr } from "./utils";

const MovieCard = props => {
  const { title, poster_path, vote_average, id, release_date} = props.item;
  const poster = poster_path != null ? `http://image.tmdb.org/t/p/w185${poster_path}` : defaultposter;
  const year = release_date.substring(0, 4);

    // form
    const [form, setForm] = useState({
      selectedtitle: "",
      selectedposter: "",
      selectedid: "",
    });

    // These methods will update the state properties.
    function updateForm(value) {
      return setForm((prev) => {
        return { ...prev, ...value };
      });
    }

    // This function will handle the submission.
    async function onSubmit(e) {
      e.preventDefault();
      const newPerson = { ...form };

      // await fetch("http://localhost:4000/record/add", {
      //   method: "POST",
      //   headers: {
      //     "Content-Type": "application/json",
      //   },
      //   body: JSON.stringify(newPerson),
      // })
      // .catch(error => {
      //   window.alert(error);
      //   return;
      // });

      setForm({ selectedtitle: "", selectedposter: "", selectedid: ""});
    }

  return (
    <div class="table-responsive table-hoverable" className={classes.Container}>
      <form onSubmit={onSubmit}>
      <div className="form-group">
        <tr class="ml-auto" onClick={onSubmit}> 
          <td class="col-sm-1"><img src={poster} style={{height: 80}}></img></td>
          <td class="ml-auto">
            <b>{title}</b> <p class="text-muted">({year})</p>
          </td>
        </tr>
      </div>
      </form>
    </div>
  );

};

export default MovieCard;
