import React, { useEffect, useState } from "react";
import axios from 'axios';
import defaultposter from './search/defaultposter.jpeg';
import Carousel from 'react-grid-carousel'

export default function Discover() {
  const [trendingList, setTrendingList] = useState([{
    filmid: "",
    title: "",
    poster: "",
  }]);

  useEffect(() => {
    async function fetchData() {
        const trending = await axios(
            `https://api.themoviedb.org/3/trending/movie/day?api_key=dbc0a6d62448554c27b6167ef7dabb1b`
        );
        // console.log(trending);

        const trendingArr = [];
        for (let i = 0; i < trending.data.results.length; i++) {
          const posterPath = trending.data.results[i].poster_path != null ? `http://image.tmdb.org/t/p/original${trending.data.results[i].poster_path}` : defaultposter
          trendingArr.push({
            filmid: trending.data.results[i].id, 
            title: trending.data.results[i].title, 
            poster: posterPath
          })
        }
        setTrending(trendingArr);
        console.log(trending);
    }
    fetchData();
    return;
}, []);

  const generateTrending = () => {
    return (
      <div id="trending">
        {trending.map((trending) => (
          <Carousel.Item key={trending.filmid}>
            <img height="300px" src={trending.poster} />
            <p>{trending.title}</p>
          </Carousel.Item>            
        ))}
      </div>
    );
  }

  return (
    <div className="table-container" style={{marginTop: 100, marginLeft: 300, marginRight: 300}}>
      <h3>Recommendations</h3>
      <div>
      <Carousel cols={2} rows={1} gap={10} loop>
        {generateTrending()}
        {/* <Carousel.Item>
          <img width="100%" src="https://picsum.photos/800/600?random=1" />
