import React, { useState } from "react";
import defaultposter from './defaultposter.jpeg';

import classes from "./movie.module.css";
import { truncStr } from "./utils";

function myFunction(x) {
  alert("Row index is: " + x.rowIndex);
}

const MovieCard = props => {
  const { title, poster_path, vote_average, id, release_date} = props.item;
  const poster = poster_path != null ? `http://image.tmdb.org/t/p/w185${poster_path}` : defaultposter;
  const year = release_date.substring(0, 4);
  const title1 = title.title;

  var [selectedTitle, setSelectedTitle] = React.useState("");

  const updateSelectedTitle = (select) => {
    setSelectedTitle(select);
    console.log("selected title: ");
    console.log(selectedTitle);
    console.log("select: ");
    console.log(select);
  }

  return (
    // <a href={`https://google.com/${title}`}>
    <tr onClick={() => {
      setSelectedTitle({title1});
      updateSelectedTitle({title});
    }}>
      <td class="col-sm-1"><img src={poster} style={{height: 80}}></img></td>
      <td><b>{title}</b> <p class="text-muted">({year})</p></td>
    </tr>
    // </a>
  );

};

export default MovieCard;
