import React, { Component, useState, useRef, useEffect } from 'react';
import { useParams, useNavigate } from "react-router-dom";
import axios from 'axios';
import defaultposter from './search/defaultposter.jpeg';

import TextField from '@mui/material/TextField';

export default function Info() {
    const params = useParams();
const navigate = useNavigate();
//   state = {
//     movies: null,
//     loading: false,
//     value: ''
//   };

//   search = async val => {
//     this.setState({ loading: true });

//     const res = await axios(
//       `https://api.themoviedb.org/3/movie/2062?api_key=dbc0a6d62448554c27b6167ef7dabb1b&language=en-US`
//     );
//     const movies = await res.data.results;

//     this.setState({ movies, loading: false });
//   };

    const [title, setTitle] = useState("");
    const [year, setYear] = useState("");
    const [tagline, setTagline] = useState("");
    const [overview, setOverview] = useState("");
    const [poster, setPoster] = useState("");

    useEffect(() => {
        async function fetchData() {
        //   const id = params.id.toString();
        const result = await axios(
            `https://api.themoviedb.org/3/movie/2062?api_key=dbc0a6d62448554c27b6167ef7dabb1b&language=en-US`
        );
        console.log(result.data);
        setTitle(result.data.title);
        setYear(result.data.release_date.substring(0, 4));
        setTagline(result.data.tagline);
        setOverview(result.data.overview);
        // posterPath = result.data.poster_path != null ? `http://image.tmdb.org/t/p/original${result.data.poster_path}` : defaultposter
        setPoster(`http://image.tmdb.org/t/p/original${result.data.poster_path}`);

        // const data = result.data;

        //   if (!result.ok) {
        //     const message = `An error has occured: ${result.statusText}`;
        //     window.alert(message);
        //     return;
        //   }

        //   const info = JSON.parse(result.data);
        //   if (!info) {
        //     window.alert(`Film not found`);
        //     navigate("/");
        //     return;
        //   }

        //   setForm(list);
        //   let sizes_arr = JSON.parse(list.list); 
        }

        fetchData();

        return;
    });

    return (
      <div style={{marginTop: 100, marginLeft: 300, marginRight: 300}}>
        <div className="container">
            <h3 style={{display: "inline", marginRight: 10}}>{title}</h3>
            <p class="text-muted" style={{fontSize: 22, display: "inline"}}>({year})</p>
            <br></br><br></br>
            <div class="row">
                <div class="col-4 nopadding">
                    <img src={poster} style={{width: 300}} class="img-fluid"/>
                </div>
                <div class="col">
                    <h5>{tagline}</h5>
                    <p>{overview}</p>
                </div>
            </div>
        </div>
      </div>
    );
  }
