import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import { Link } from "react-router-dom";
import poster from './poster.jpg';

import Rating from '@mui/material/Rating';
import { format } from 'date-fns';

import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';

export default function View() {
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };

  // rating and date
  const [rating, setRating] = React.useState(null);
  const [value1, setValue1] = React.useState(null);

  const [selectedId, setSelectedId] = React.useState();
  const [records, setRecords] = useState([]);
  const [show, setShow] = useState(false);

  const dateRef = React.useRef();
  const ratingRef = React.useRef();

  // og
  const [form, setForm] = useState({
    name: "",
    position: "",
    date: "",
    rating: "",
    records: [],
  });
  const params = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const id = params.id.toString();
      const response = await fetch(`http://localhost:4000/record/${params.id.toString()}`);

      if (!response.ok) {
        const message = `An error has occured: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const record = await response.json();
      if (!record) {
        window.alert(`Record with id ${id} not found`);
        navigate("/");
        return;
      }

      setForm(record);
      setValue1(record.date);
      setRating(record.rating);
    }

    fetchData();

    return;
  }, [params.id, navigate]);

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  async function onSubmit(e) {
    e.preventDefault();
    const d = dateRef.current.value;
    const dt = isNaN(d) ? d : format(new Date(+d), 'MM/dd/yyyy');

    const editedPerson = {
      name: form.name,
      position: form.position,
      date: dt,
      rating: ratingRef.current.value,
    };

    // This will send a post request to update the data in the database.
    await fetch(`http://localhost:4000/update/${params.id}`, {
      method: "POST",
      body: JSON.stringify(editedPerson),
      headers: {
        'Content-Type': 'application/json'
      },
    });

    navigate("/");
  }

  const Record = (props) => (
    <tr>
      <td>{props.record.date}</td>
      <td>{props.record.name}</td>
      <td>{props.record.position}</td>
      <td>{props.record.rating}</td>
      <td>
  {/* OG ADD/DELETE */}
        {/* <Link className="btn btn-link" to={`/edit/${props.record._id}`}>Edit</Link> | */}
        {/* <button className="btn btn-link"
          onClick={() => {
            // setShowModal(true);
            props.deleteRecord(props.record._id);
          }}>Delete</button> */}

        <Link className="btn btn-primary" to={`/review/${props.record._id}`}>View</Link>

        <Link className="btn btn-primary" to={`/edit/${props.record._id}`}>Edit</Link>

        <button type="button" class="btn btn-primary ml-3" data-toggle="modal" data-target="#exampleModal" onClick={() => {
                    // idRef = props.record._id;
                    setSelectedId(props.record._id);
                }}>
          Delete
        </button>

        <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div class="modal-dialog" role="document">
            <div class="modal-content">
              <div class="modal-header border-0">
                {/* <h5 class="modal-title" id="exampleModalLabel">Woah!</h5> */}
                <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div class="modal-body text-center" style={{fontSize: 20}}>
              Are you sure you want to delete your review?
              </div>
              <div class="modal-footer border-0">
                <button type="button" class="btn btn-secondary" data-dismiss="modal">Cancel</button>
                <button type="button" class="btn btn-primary" onClick={() => {
                    fetch(`http://localhost:4000/${selectedId}`, {
                      method: "DELETE"
                    });

                    const newRecords = records.filter((el) => el._id !== selectedId);
                    setRecords(newRecords);

                    // props.deleteRecord(props.record._id);
                }} data-dismiss="modal">Delete</button>
              </div>
            </div>
          </div>
        </div>

      </td>
    </tr>
  );

  // This following section will display the form that takes input from the user to update the data.
  return (
    <div style={{marginTop: 50, marginLeft: 300, marginRight: 300}}>
    <div class="container">
    <h3>{form.name}
    <div class="float-right">
      <Button
        id="basic-button"
        aria-controls={open ? 'basic-menu' : undefined}
        aria-haspopup="true"
        aria-expanded={open ? 'true' : undefined}
        onClick={handleClick}
      >
        •••
      </Button>
      <Menu
        id="basic-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          'aria-labelledby': 'basic-button',
        }}
      >

        <Link to={`/review/${props.record._id}`} style={{ textDecoration: 'none' }}>
        {/* /edit/64189acfa157bf2a5832545e */}
        <MenuItem>Edit</MenuItem></Link>

        <MenuItem onClick={handleClose}>Delete</MenuItem>
      </Menu>
    </div>
    </h3>
    </div>
    <br></br>
    <div class="container">
        <div class="row">
            <div class="col-4 nopadding">
                {/* placeholder poster for now!! */}
                <img src={poster} class="img-fluid"/>
            </div>
            <div class="col">
            <p> watched on {value1}
            <div class="float-right"><Rating
                    name="simple-controlled"
                    defaultValue={rating}
                    value={rating}
                    size="medium"
                    readOnly
                /></div>
            </p>
            <p>{form.position}</p> 
            </div>
        </div>
    </div>

    </div>
  );
}
