import React, { useState } from "react";
import { useNavigate } from "react-router";
import poster from './poster.jpg';

import Rating from '@mui/material/Rating';
import TextField from '@mui/material/TextField';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { format } from 'date-fns';
import Input from '@mui/material/Input';
import Typography from '@mui/material/Typography';

const ariaLabel = { 'aria-label': 'description' };

export default function CreateList() {
  // rating and date
  const [rating, setRating] = React.useState();
  const [value1, setValue1] = React.useState(null);

  const updateRating = (newRating) => {
    setRating(newRating);
    console.log(rating);
  }

  const dateRef = React.useRef();
  const ratingRef = React.useRef();

  // form
  const [form, setForm] = useState({
    name: "",
    review: "",
    date: "",
    rating: "",
  });
  const navigate = useNavigate();

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();
    // if no date entered, default is today's date
    const d = isNaN(dateRef.current.value) ? dateRef.current.value : new Date();
    var dt = new Date(+d);

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newPerson = { ...form };
    newPerson.date = format(dt, 'MM/dd/yyyy');
    newPerson.rating = ratingRef.current.value;

    await fetch("http://localhost:4000/list/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPerson),
    })
    .catch(error => {
      window.alert(error);
      return;
    });

    setForm({ name: "", review: "", date: "", rating: "" });
    navigate("/");
  }

  // This following section will display the form that takes the input from the user.
  return (
    <div style={{marginTop: 100, marginLeft: 300, marginRight: 300}}>
      <div class="container">
        {/* <h3> */}
        {/* <Typography variant="h3" component="h3"> */}
        <TextField
          fullWidth
          id="outlined-multiline-flexible"
          placeholder="New List"
          multiline
          rows={1}
          variant="standard"
          InputProps={{ disableUnderline: true }}
          inputProps={{style: {fontSize: 28, fontWeight: 500}}} // font size of input text
          InputLabelProps={{style: {fontSize: 28, fontWeight: 500}}} // font size of input label
        />
          {/* <Input fullWidth placeholder="New List" inputProps={ariaLabel} disableUnderline={true}/> */}
        {/* </Typography></h3> */}
        <TextField
          fullWidth
          id="outlined-multiline-flexible"
          placeholder="ADD DESCRIPTION..."
          multiline
          rows={3}
          maxRows={3}
          variant="standard"
          InputProps={{ disableUnderline: true }}
        />    
      </div> <br></br>
      <div class="container"><TextField fullWidth id="outlined-basic" label="ADD FILMS..." variant="outlined" /></div> 
      <br></br>
      <div className="form-group text-right container">
        <a href="/lists" class="btn btn-light mr-3" role="button">CANCEL</a>
        <input
          type="submit"
          value="LOG"
          className="btn btn-success"
        />
      </div>
    </div>
  );
}
