import React, { Component } from "react";
import Select from "react-select";
import axios from "axios";


const apikey = "37e7a5cb2b4f66257a0b6632a59060a5";

export default class MoviePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedValue: {},
      selectOptions: [],
      search_value: '',
      search_results: [],
      id: "",
      name: ""
    };
  }

  async getOptions() {
    const res = await axios.get("https://api.themoviedb.org/3/genre/movie/list?api_key="+ apikey +"&language=en-US");
    const data = res.data;

    const options = data.map((d) => ({
      value: d.id,
      label: d.name
    }));

    this.setState({ selectOptions: options });
  }

  handleChange(e) {
    console.log(e);
    this.setState({ selectedValue: e });
  }

  componentDidMount() {
    this.getOptions();
  }

  buttonClick = () => {
    const valueToSet = this.state.selectOptions.find((row) => {
      return row.value === 2 && row.label === "more info";
    });

    if (valueToSet) {
      this.handleChange(valueToSet);
    }
  };

  render() {
    var list = this.state.search_results.map(movieItem =>
        <div key={movieItem.id} className="movieContent mx-5" display="flex" justifyContent="center" alignItems="center">
            {(movieItem.poster_path!=null)?
                (<div className='container-fluid movie-app'>
                    <div className='row d-flex align-items-center mt-4 mb-4'>
                            <Link to={'/moviePage'}>
                                <img src={"https://image.tmdb.org/t/p/w185"+movieItem.poster_path+""} alt={movieItem.title}/>
                                <h3>{movieItem.title}</h3>
                            </Link>
                    </div>
                </div>):
                (<div className="poster"/>)
            }
        </div>);
        return list
  }
}
