      //   return;
      // });

      setForm({ selectedtitle: "", selectedposter: "", selectedid: ""});
      navigate("/");
    }

  return (
    <div class="table-responsive">
        <tr class="ml-auto"> 
          <td class="col-sm-1"><img src={poster} style={{height: 80}}></img></td>
          <td class="ml-auto">
            <b>{title}</b> <p class="text-muted">({year})</p>
          </td>
        </tr>
    </div>
  );

};

export default MovieCard;
