import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Search from "./searchList/search";
import {
  Button,
  Card,
  Grid,
  Input,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Stack,
  Snackbar,
  Slide,
} from "@mui/material";

import DeleteIcon from '@mui/icons-material/Delete';

import TextField from '@mui/material/TextField';

export default function CreateList() {
  // table stuff
  const [rows, setRows] = useState([
    {
      id: 1,
      item: "",
    }
  ]);

  const generateRow = () => {
    return {
      id: rows.length + 1,
      item: "",
    }
  }

  const handleDeleteRow = (event, id) => {
    setRows(rows => {
      rows[id - 1].item = "";
      for (let i = id - 1; i < rows.length; i++) {
        rows[i].id--;
      }
    });

    setRows((prevRows) => {
      return [
        ...rows.slice(0, id - 1),
        ...rows.slice(id),
      ];
    });
  }

  const handleChange = (e, id) => {
    let value = e.target.value;

    if (id === rows.length) {
      if (value !== "") {
        setRows((prevRows) => [...prevRows, generateRow()]);
      }
    } else if (id + 1 === rows.length) {
      if (value === "") {
        handleDeleteRow(e, id+1);
      }
    }
    setRows((prevRows) => {
      return prevRows.map((row, index) =>
        index === id - 1 ? { ...row, item: value} : row,
      );
    });
  }

  const handleSave = () => {
    let arr = [];
    if (rows[rows.length - 1].item === "") {
      arr = rows.slice(0, -1);
    } else {
      arr = rows;
    }

    let items = [];
    for (let i = 0; i < arr.length; i++) {
      items.push(arr[i].item);
    }

    window.sessionStorage.setItem("list", JSON.stringify(items));
  }

  const generateTable = () => {
    return (
      <Table>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.id}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                <Input placeholder={"Add Item"} onChange={(e) => handleChange(e, row.id)} value={row.item}/>
              </TableCell>
              <TableCell component="th" scope="row">
                {
                  row.id === rows.length ? (<></>) : (
                    <IconButton onClick={(e) => handleDeleteRow(e, row.id)}>
                      <DeleteIcon />
                    </IconButton>
                  )
                }
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    );
  }

  const [tableData, setTableData] = useState(generateTable());

  useEffect(() => {
    setTableData(generateTable());
  }, [rows])

  // read info from local session and put into table
  useEffect(() => {
    if (window.sessionStorage.getItem("list")) {
      let sizes_arr = JSON.parse(window.sessionStorage.getItem("list"));
      sizes_arr.push("")
      let rows_arr = [];
      for (let i = 0; i < sizes_arr.length; i++) {
        rows_arr.push({
          id: i+1,
          item: sizes_arr[i]
        })
      }

      setRows(rows_arr);
      setTableData(generateTable())
    }
  }, [])

  // form
  const [form, setForm] = useState({
    name: "",
    description: "",
    list: [],
  });
  const navigate = useNavigate();

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  // This function will handle the submission.
  async function onSubmit(e) {
    let arr = [];
    if (rows[rows.length - 1].item === "") {
      arr = rows.slice(0, -1);
    } else {
      arr = rows;
    }

    let items = [];
    for (let i = 0; i < arr.length; i++) {
      items.push(arr[i].item);
    }

    e.preventDefault();
    // set default list name
    const n = isNaN(form.name) ? form.name : "Untitled List";

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newList = { ...form };
    newList.name = n;
    newList.list = items;

    await fetch("http://localhost:4000/list/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newList),
    })
    .catch(error => {
      window.alert(error);
      return;
    });

    setForm({ name: "", description: "" });
    navigate("/lists");
  }

  // This following section will display the form that takes the input from the user.
  return (
    <div style={{marginTop: 100, marginLeft: 300, marginRight: 300}}>
      <form onSubmit={onSubmit}>
      <div class="container">
        <TextField
          fullWidth
          id="outlined-multiline-flexible"
          placeholder="New List"
          multiline
          rows={1}
          variant="standard"
          InputProps={{ disableUnderline: true }}
          inputProps={{style: {fontSize: 28, fontWeight: 500}}} // font size of input text
          InputLabelProps={{style: {fontSize: 28, fontWeight: 500}}} // font size of input label
          onChange={e => updateForm({ name: e.target.value })}
          type="text"
        /> 
        <br></br><br></br>
        <TextField
          fullWidth
          id="outlined-multiline-flexible"
          placeholder="ADD DESCRIPTION..."
          multiline
          rows={3}
          maxRows={3}
          variant="standard"
          InputProps={{ disableUnderline: true }}
          onChange={e => updateForm({ description: e.target.value })}
          type="text"
        />    
      </div> <br></br>
      <div class="container"><TextField fullWidth id="outlined-basic" label="ADD FILMS..." variant="outlined" hidden/></div> 
      <div class="container"><Search/></div>
      <div class="container">
        {/* <table className="table table-hover" id="buildinglist">
          <tbody></tbody>
        </table> */}
            <Grid container>
              <Grid item lg={12} justifyContent="center" display="flex">
                <Card>
                  <TableContainer>
                    {tableData} {/* this variable will change to contain all data */}
                    <Stack direction={"row"}>
                      <Button onClick={handleSave}>
                        Save
                      </Button>
                    </Stack>
                  </TableContainer>
                </Card>
              </Grid>
            </Grid>
      </div>

      <div className="form-group text-right container">
        <a href="/lists" class="btn btn-light mr-3" role="button">CANCEL</a>
        <input
          type="submit"
          value="SAVE"
          className="btn btn-success"
        />
      </div>   
      </form>  
    </div>
  );
}
