import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";

import Rating from '@mui/material/Rating';

import TextField from '@mui/material/TextField';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { format } from 'date-fns';

export default function Edit() {
  // rating and date
  const [rating, setRating] = React.useState(null);
  const [value1, setValue1] = React.useState(null);

  const updateRating = (newRating) => {
    setRating(newRating);
    console.log(rating);
  }
  const [inputs, setInputs] = React.useState({});

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const r = ratingRef.current.value;
    const d = dateRef.current.value;
    var dt = new Date(+d);
    alert(r + ' ' + format(dt, 'MM/dd/yyyy'));
  }

  const dateRef = React.useRef();
  const ratingRef = React.useRef();

  // og
  const [form, setForm] = useState({
    name: "",
    position: "",
    date: "",
    rating: "",
    records: [],
  });
  const params = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const id = params.id.toString();
      const response = await fetch(`http://localhost:4000/record/${params.id.toString()}`);

      if (!response.ok) {
        const message = `An error has occured: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const record = await response.json();
      if (!record) {
        window.alert(`Record with id ${id} not found`);
        navigate("/");
        return;
      }

      setForm(record);
      setValue1(record.date);
      setRating(record.rating);
    }

    fetchData();

    return;
  }, [params.id, navigate]);

  // These methods will update the state properties.
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  async function onSubmit(e) {
    e.preventDefault();
    const d = dateRef.current.value;
    const dt = isNaN(d) ? d : format(new Date(+d), 'MM/dd/yyyy');

    const editedPerson = {
      name: form.name,
      position: form.position,
      date: dt,
      rating: ratingRef.current.value,
    };

    // This will send a post request to update the data in the database.
    await fetch(`http://localhost:4000/update/${params.id}`, {
      method: "POST",
      body: JSON.stringify(editedPerson),
      headers: {
        'Content-Type': 'application/json'
      },
    });

    navigate("/");
  }

  // This following section will display the form that takes input from the user to update the data.
  return (
    <div style={{marginTop: 50, marginLeft: 300, marginRight: 300}}>
      <h3>{form.name}       
            <Rating
                name="simple-controlled"
                defaultValue={rating}
                value={rating}
                // precision={0.5}
                size="medium"
                onChange={(event, newValue) => updateRating(newValue)
                }
            />
        </h3>

      <form onSubmit={onSubmit}>
        <div className="form-group">
          <label htmlFor="name">I WATCHED... {form.name}</label>
          {/* <input
            type="text"
            className="form-control"
            id="name"
            value={form.name}
            onChange={(e) => updateForm({ name: e.target.value })}
          /> */}
        </div>

        <div className="form-group row" style={{marginLeft: 5, marginRight: 5}}>
          <label htmlFor="date" className="col-form-label">ON:</label>
          <div className="col-lg">
            <input
              name="entry"
              type="text"
              value={value1}
              ref={dateRef}
              hidden
              readOnly
            />

            <LocalizationProvider dateAdapter={AdapterDayjs}>
              <DatePicker
                mask="__/__/____"
                value={value1}
                onChange={(newValue) => {
                  setValue1(newValue);
                }}
                renderInput={(params) => <TextField size="small" {...params} />}
              />
            </LocalizationProvider>
          </div>

          <div>
          <input
              name="rating"
              type="number"
              value={rating}
              ref={ratingRef}
              hidden
              readOnly
            />

            <Rating
            name="simple-controlled"
            defaultValue={rating}
            value={rating}
            // precision={0.5}
            size="large"
            onChange={(event, newValue) => updateRating(newValue)
            }
            />
          </div>
        </div>

        <div className="form-group">
          <textarea
            type="text"
            className="form-control"
            id="position"
            rows="5"
            value={form.position}
            onChange={(e) => updateForm({ position: e.target.value })}
          />
        </div>

        <div className="form-group text-right">
          <a href="/" class="btn btn-secondary mr-3" role="button">CANCEL</a>

          <input
            type="submit"
            value="SAVE"
            className="btn btn-primary"
          />
        </div>
      </form>
    </div>
  );
}
