    //   autoComplete="off"
    // >
    //   <div>
    //     <TextField
    //       fullWidth
    //       sx={{
    //         width: 220,
    //         mx: 'auto'
    //       }}
    //       required
    //       id="outlined-required"
    //       label="E-mail Address"
    //       value={email}
    //       onChange={(e) => setEmail(e.target.value)}
    //     />
    //     <TextField
    //       fullWidth
    //       sx={{
    //         width: 220,
    //         mx: 'auto'
    //       }}
    //       required
    //       id="outlined-required"
    //       label="Password"
    //       value={password}
    //       onChange={(e) => setPassword(e.target.value)}
    //     />
    //     <Button
    //       fullWidth
    //       sx={{
    //         mt: 2,
    //         width: 220,
    //         mx: 'auto',
    //         ml: 1
    //       }}
    //       variant="contained"
    //       color="success"
    //       onClick={() => logInWithEmailAndPassword(email, password)}>
    //       LOG IN
    //     </Button>
    //   </div>
    // </Box>

    <ThemeProvider theme={theme}>
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Admin Log In
        </Typography>
        <Box component="form" noValidate sx={{ mt: 1 }}>
          <TextField
            required
            fullWidth
            id="outlined-required"
            label="E-mail Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            margin="normal"
            // required
            // fullWidth
            // id="email"
            // label="Email Address"
            // name="email"
            // value={email}
            // onChange={(e) => setEmail(e.target.value)}
            // autoComplete="email"
            autoFocus
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            autoComplete="current-password"
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
            onClick={() => logInWithEmailAndPassword(email, password)}
          >
            LOG IN
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
          </Grid>
        </Box>
      </Box>
    </Container>
  </ThemeProvider>
  );
}

export default Login;
