  useEffect(() => {
    async function fetchData() {
      const id = params.id.toString();
      const response = await axios(`https://api.themoviedb.org/3/movie/2062?api_key=dbc0a6d62448554c27b6167ef7dabb1b&language=en-US`);

      if (!response.ok) {
        const message = `An error has occured: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const list = await response.json();
      if (!list) {
        window.alert(`List with id ${id} not found`);
        navigate("/");
        return;
      }

      setForm(list);

      let sizes_arr = JSON.parse(list.list); 

      let rows_arr = [];
      rows_arr.push({
        id: 1,
        item: {title: "", poster: ""}
      })
      for (let i = 0; i < sizes_arr.length; i++) {
        rows_arr.push({
          id: i+2,
          item: sizes_arr[i]
        })
      }
      console.log(rows_arr);

      setRows(rows_arr);
      setTableData(generateTable());
    }

    fetchData();

    return;
  }, [params.id, navigate]);

